import React from 'react';
import { MapContainer, TileLayer, Marker, Popup } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// Fix for default marker icon
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconRetinaUrl: require('leaflet/dist/images/marker-icon-2x.png'),
  iconUrl: require('leaflet/dist/images/marker-icon.png'),
  shadowUrl: require('leaflet/dist/images/marker-shadow.png')
});

function HospitalMap({ hospitals, userLocation }) {
  if (!hospitals || hospitals.length === 0) {
    return <div>No hospitals to display</div>;
  }

  const center = userLocation || [hospitals[0].latitude, hospitals[0].longitude];

  return (
    <div style={{ height: '400px', width: '100%', marginTop: '20px', marginBottom: '20px' }}>
      <MapContainer 
        center={center} 
        zoom={12} 
        style={{ height: '100%', width: '100%' }}
      >
        <TileLayer
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
        />
        
        {hospitals.map((hospital) => (
          <Marker 
            key={hospital.hospital_id} 
            position={[hospital.latitude, hospital.longitude]}
          >
            <Popup>
              <div>
                <strong>{hospital.hospital_name}</strong><br/>
                {hospital.address}<br/>
                {hospital.phone && `ðŸ“ž ${hospital.phone}`}<br/>
                {hospital.distance && `Distance: ${hospital.distance.toFixed(2)} km`}
              </div>
            </Popup>
          </Marker>
        ))}
      </MapContainer>
    </div>
  );
}

export default HospitalMap;
