import React, { useState } from 'react';
import axios from 'axios';
import HospitalMap from '../HospitalMap';

function DiabetesForm() {
  const [formData, setFormData] = useState({
    glucose: '',
    bmi: '',
    age: '',
    insulin: '',
    pregnancies: '',
    bloodPressure: '',
    skinThickness: '',
    dpf: ''
  });
  
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setLoading(true);
    setResult(null);

    try {
      const token = localStorage.getItem('token');
      const response = await axios.post(
        'http://localhost:5000/api/predict/diabetes',
        formData,
        { headers: { 'Authorization': `Bearer ${token}` } }
      );
      
      setResult(response.data);
    } catch (error) {
      setError(error.response?.data?.error || 'Prediction failed');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="container">
      <div className="prediction-container">
        <h2>ü©∫ Diabetes Risk Prediction</h2>
        <p>Enter your health metrics for diabetes risk assessment</p>
        
        {error && <div className="error-message">{error}</div>}
        
        <form onSubmit={handleSubmit} className="prediction-form">
          <div className="form-row">
            <div className="form-group">
              <label>Glucose Level (mg/dL):</label>
              <input
                type="number"
                step="0.01"
                required
                value={formData.glucose}
                onChange={(e) => setFormData({...formData, glucose: e.target.value})}
              />
            </div>
            
            <div className="form-group">
              <label>BMI:</label>
              <input
                type="number"
                step="0.01"
                required
                value={formData.bmi}
                onChange={(e) => setFormData({...formData, bmi: e.target.value})}
              />
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-group">
              <label>Age:</label>
              <input
                type="number"
                required
                value={formData.age}
                onChange={(e) => setFormData({...formData, age: e.target.value})}
              />
            </div>
            
            <div className="form-group">
              <label>Insulin Level (mu U/ml):</label>
              <input
                type="number"
                step="0.01"
                required
                value={formData.insulin}
                onChange={(e) => setFormData({...formData, insulin: e.target.value})}
              />
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-group">
              <label>Number of Pregnancies:</label>
              <input
                type="number"
                required
                value={formData.pregnancies}
                onChange={(e) => setFormData({...formData, pregnancies: e.target.value})}
              />
            </div>
            
            <div className="form-group">
              <label>Blood Pressure (mm Hg):</label>
              <input
                type="number"
                required
                value={formData.bloodPressure}
                onChange={(e) => setFormData({...formData, bloodPressure: e.target.value})}
              />
            </div>
          </div>
          
          <div className="form-row">
            <div className="form-group">
              <label>Skin Thickness (mm):</label>
              <input
                type="number"
                step="0.01"
                required
                value={formData.skinThickness}
                onChange={(e) => setFormData({...formData, skinThickness: e.target.value})}
              />
            </div>
            
            <div className="form-group">
              <label>Diabetes Pedigree Function:</label>
              <input
                type="number"
                step="0.001"
                required
                value={formData.dpf}
                onChange={(e) => setFormData({...formData, dpf: e.target.value})}
              />
            </div>
          </div>
          
          <button type="submit" className="btn btn-primary btn-large" disabled={loading}>
            {loading ? 'Analyzing...' : 'Predict Diabetes Risk'}
          </button>
        </form>
        
        {result && (
          <div className="result-container">
            <div className={`result-card ${result.prediction === 'Positive' ? 'result-positive-card' : 'result-negative-card'}`}>
              <h3>Prediction Result</h3>
              <div className="result-grid">
                <div className="result-item">
                  <span className="result-label">Result:</span>
                  <span className="result-value">{result.prediction}</span>
                </div>
                <div className="result-item">
                  <span className="result-label">Confidence:</span>
                  <span className="result-value">{(result.confidence * 100).toFixed(2)}%</span>
                </div>
                <div className="result-item">
                  <span className="result-label">Risk Level:</span>
                  <span className="result-value">{result.riskPercentage}%</span>
                </div>
                <div className="result-item">
                  <span className="result-label">Severity:</span>
                  <span className={`severity-badge severity-${result.severity.toLowerCase().replace(' ', '-')}`}>
                    {result.severity}
                  </span>
                </div>
              </div>
              
              {result.emergencyRequired && (
                <div className="emergency-alert">
                  ‚ö†Ô∏è <strong>Emergency:</strong> Immediate medical attention recommended
                </div>
              )}
              
              <div className="recommendations">
                <h4>Recommendations:</h4>
                <p>{result.recommendations}</p>
              </div>
            </div>
            
            {result.nearbyHospitals && result.nearbyHospitals.length > 0 && (
              <div className="hospitals-container">
                <h3>üè• Nearest Hospitals</h3>
                <HospitalMap 
                  hospitals={result.nearbyHospitals}
                  userLocation={[result.nearbyHospitals[0]?.latitude || 33.6, result.nearbyHospitals[0]?.longitude || 73.0]}
                />
                
                <div className="hospitals-list">
                  {result.nearbyHospitals.map((hospital, index) => (
                    <div key={hospital.hospital_id} className="hospital-card">
                      <h4>{index + 1}. {hospital.hospital_name}</h4>
                      <p>üìç {hospital.address}, {hospital.city}</p>
                      <p>üìû {hospital.phone}</p>
                      <p>üìè Distance: {hospital.distance?.toFixed(2)} km</p>
                      <p>‚≠ê Rating: {hospital.rating}/5.0</p>
                    </div>
                  ))}
                </div>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
}

export default DiabetesForm;
